        for (let i = 0; i < shipCoordinates.length; i++) {
          if (shipCoordinates[i].coordinate === coordinateChosenByEnemy) {
            shipCoordinates[i].hit = true;
          }
        }

        isSunk(); 
      };

      return {shipCoordinates, hit}; 
